//nolint:lll
const (
	VyprvpnCertificate = ""
)

func VyprvpnRegionChoices() (choices []string) {
	servers := VyprvpnServers()
	choices = make([]string, len(servers))
	for i := range servers {
		choices[i] = servers[i].Region
	}
	return makeUnique(choices)
}

